try:  # import the core dragonfly dependencies
    from dragonfly.model import Model
    from dragonfly.building import Building
    from dragonfly.story import Story
    from dragonfly.room2d import Room2D
    from dragonfly.context import ContextShade
except ImportError as e:
    raise ImportError('\nFailed to import dragonfly:\n\t{}'.format(e))

try:  # import the core honeybee dependencies
    from honeybee.facetype import Floor
except ImportError as e:
    raise ImportError('\nFailed to import honeybee:\n\t{}'.format(e))

try:  # import the ladybug_{{cad}} dependencies
    from ladybug_{{cad}}.fromgeometry import from_face3d_to_wireframe
    from ladybug_{{cad}}.{{plugin}} import all_required_inputs
except ImportError as e:
    raise ImportError('\nFailed to import ladybug_{{cad}}:\n\t{}'.format(e))


def room_2d_geometry(room_2ds):
    """Get {{Cad}} geometry curves from a list of Room2Ds."""
    return [curve for room in room_2ds for curve in
            from_face3d_to_wireframe(room.floor_geometry)]


def room_3d_geometry(room_3ds):
    """Get {{Cad}} geometry from a list of 3D Rooms."""
    room_geo = []
    for room in room_3ds:
        for face in room.faces:
            if isinstance(face.type, Floor):
                room_geo.extend(from_face3d_to_wireframe(face.geometry))
    return room_geo


def context_shade_geometry(context_shades):
    """Get {{Cad}} geometry curves from a list of ContextShades."""

    faces =  [face for shd_geo in context_shades
            for face in shd_geo.geometry]
    return [curve for face in faces for curve in from_face3d_to_wireframe(face)]


